'use strict';

module.exports = async (app, module, req, res) =>
{
  const {ProdSerialNumber} = module;

  const conditions = {};
  const hint = {};
  const {sn} = req.body;

  if (/^[A-Z0-9]{4}\.[0-9]{9}\.[0-9]{4}$/i.test(sn))
  {
    conditions._id = sn;
    hint._id = 1;
  }
  else
  {
    conditions.bom = sn;
    hint.bom = 1;
  }

  const psn = await ProdSerialNumber
    .findOne(conditions)
    .select({_id: 1})
    .hint(hint)
    .lean()
    .exec();

  res.json(psn ? psn._id : null);
};
