'use strict';

exports.name = 'QiActionStatus';

exports.setUp = (app, mongoose) =>
{
  const qiActionStatusSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    position: {
      type: Number,
      default: 0
    }
  }, {
    id: false
  });

  qiActionStatusSchema.statics.TOPIC_PREFIX = 'qi.actionStatuses';
  qiActionStatusSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, qiActionStatusSchema);
};
