'use strict';

exports.name = 'QiFault';

exports.setUp = (app, mongoose) =>
{
  const qiFaultSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true,
      default: ''
    },
    active: {
      type: Boolean,
      default: true
    },
    weight: {
      type: Number,
      default: 0
    }
  }, {
    id: false
  });

  qiFaultSchema.statics.TOPIC_PREFIX = 'qi.faults';
  qiFaultSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, qiFaultSchema);
};
