'use strict';

exports.name = 'QiKind';

exports.setUp = (app, mongoose) =>
{
  const qiKindSchema = new mongoose.Schema({
    name: {
      type: String,
      required: true,
      trim: true
    },
    division: {
      type: String,
      ref: 'Division',
      default: null
    },
    order: {
      type: Boolean,
      default: true
    },
    position: {
      type: Number,
      default: 0
    }
  }, {
    id: false
  });

  qiKindSchema.statics.TOPIC_PREFIX = 'qi.kinds';
  qiKindSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, qiKindSchema);
};
