'use strict';

exports.name = 'QiOqlWeek';

exports.setUp = (app, mongoose) =>
{
  const qiOqlWeekSchema = new mongoose.Schema({
    _id: Date,
    updatedAt: {
      type: Date,
      default: Date.now
    },
    updater: {
      type: {},
      default: null
    },
    target: {
      type: Number,
      default: 0
    },
    where: {
      type: [String],
      default: []
    },
    what: {
      type: [String],
      default: []
    },
    results: {
      type: [Number],
      default: []
    }
  }, {
    id: false,
    minimize: false
  });

  qiOqlWeekSchema.statics.TOPIC_PREFIX = 'qi.oqlWeeks';

  mongoose.model(exports.name, qiOqlWeekSchema);
};
