'use strict';

exports.name = 'QiStandard';

exports.setUp = (app, mongoose) =>
{
  const qiStandardSchema = new mongoose.Schema({
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true
    },
    active: {
      type: Boolean,
      default: true
    }
  }, {
    id: false
  });

  qiStandardSchema.statics.TOPIC_PREFIX = 'qi.standards';
  qiStandardSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, qiStandardSchema);
};
