'use strict';

const _ = require('lodash');
const nokRatioReport = require('../nokRatioReport');

module.exports = (app, module, req, res, next) =>
{
  const {
    reports,
    orgUnits
  } = module;

  const {query, reportHash} = req;
  const fromTime = reports.helpers.getTime(query.from) || null;
  const options = {
    interval: 'month',
    fromTime: reports.helpers.getTime(query.from) || null,
    toTime: reports.helpers.getTime(query.to) || null,
    kinds: _.isEmpty(query.kinds) ? [] : query.kinds.split(','),
    divisions: orgUnits.getAllByType('division')
      .filter(d => !d.deactivatedAt || !fromTime || fromTime < d.deactivatedAt)
      .map(d => d.toJSON())
  };

  reports.helpers.generateReport(
    app,
    reports,
    nokRatioReport,
    'qi/nokRatio',
    reportHash,
    options,
    (err, reportJson) =>
    {
      if (err)
      {
        return next(err);
      }

      res.type('json');
      res.send(reportJson);
    }
  );
};
