'use strict';

const step = require('h5.step');
const okRatioReport = require('../okRatioReport');

module.exports = (app, module, req, res, next) =>
{
  const {
    reports,
    orgUnits,
    settings
  } = module;

  const {query, reportHash} = req;
  const fromTime = reports.helpers.getTime(query.from) || null;
  const options = {
    interval: 'month',
    fromTime: fromTime,
    toTime: reports.helpers.getTime(query.to) || null,
    divisions: orgUnits.getAllByType('division')
      .filter(d => !d.deactivatedAt || !fromTime || fromTime < d.deactivatedAt)
      .map(d => d.toJSON()),
    whQty: null
  };

  step(
    function()
    {
      settings.findValues({_id: /^qi\.whQty\./}, 'qi.whQty.', this.next());
    },
    function(err, whQty)
    {
      if (err)
      {
        return this.done(next, err);
      }

      options.whQty = whQty;

      reports.helpers.generateReport(
        app,
        reports,
        okRatioReport,
        'qi/okRatio',
        reportHash,
        options,
        (err, reportJson) =>
        {
          if (err)
          {
            return next(err);
          }

          res.type('json');
          res.send(reportJson);
        }
      );
    }
  );
};
