'use strict';

const outgoingQualityReport = require('../outgoingQualityReport');

module.exports = (app, {reports}, req, res, next) =>
{
  const query = req.query;
  const options = {
    interval: 'week',
    week: query.week
  };

  reports.helpers.generateReport(
    app,
    reports,
    outgoingQualityReport,
    'qi/outgoingQuality',
    req.reportHash,
    options,
    (err, reportJson) =>
    {
      if (err)
      {
        return next(err);
      }

      res.type('json');
      res.send(reportJson);
    }
  );
};
