'use strict';

exports.name = 'NonBusinessDay';

exports.setUp = (app, mongoose) =>
{
  const nonBusinessDaySchema = new mongoose.Schema({
    _id: Date
  }, {
    id: false,
    versionKey: false
  });

  mongoose.model(exports.name, nonBusinessDaySchema);
};
