'use strict';

module.exports = function parseSapNumber(input)
{
  const output = parseFloat(String(input).replace(/\./g, '').replace(/\s+/g, '').replace(',', '.'));

  if (isNaN(output))
  {
    return -1;
  }

  if (output < 0)
  {
    return output;
  }

  if (input.endsWith('-'))
  {
    return output * -1;
  }

  return output;
};
