'use strict';

module.exports = (app, sapGuiModule, job, done) =>
{
  const args = [
    '--material', job.material
  ];

  if (job.plant)
  {
    args.push('--plant', job.plant);
  }

  sapGuiModule.runScript(job, 'T_MD04.exe', args, done);
};
