'use strict';

module.exports = fileContents =>
{
  const orderData = {};

  fileContents.split('\n').forEach(line =>
  {
    const parts = line.trim().split(':');
    const key = parts.shift().trim().toLowerCase().replace(/\s+/g, '_');
    const value = parts.join(':').trim();

    orderData[key] = value;
  });

  return orderData;
};
