'use strict';

exports.name = 'XData';

exports.setUp = (app, mongoose) =>
{
  const xDataSchema = new mongoose.Schema({
    _id: String,
    createdAt: Date,
    creator: {},
    title: String,
    data: {}
  }, {
    id: false,
    minimize: false
  });

  xDataSchema.statics.TOPIC_PREFIX = 'sapLaborTimeFixer.xData';

  mongoose.model(exports.name, xDataSchema);
};
