'use strict';

exports.name = 'SettingChange';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    setting: String,
    time: Date,
    user: {},
    value: {}
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'settings.changes';
  schema.statics.BROWSE_LIMIT = 100;

  schema.index({setting: 1, time: -1});

  return schema;
};
