'use strict';

module.exports = (app, module) =>
{
  const {
    user,
    express,
    Setting,
    SettingChange
  } = module;

  const canView = user.auth('SUPER');

  express.get('/settings', canView, express.crud.browseRoute.bind(null, app, Setting));

  express.get('/settings/changes', canView, express.crud.browseRoute.bind(null, app, SettingChange));
};
