'use strict';

module.exports = (app, module) =>
{
  const {express, user, SosEntry} = module;

  const canView = user.auth(`USER`);
  const canManage = user.auth(`SOS:MANAGE`);

  express.get(`/sos/entries`, canView, express.crud.browseRoute.bind(null, app, SosEntry));
  express.post(`/sos/entries`, canView, express.crud.addRoute.bind(null, app, SosEntry));
  express.get(`/sos/entries/:id`, canView, express.crud.readRoute.bind(null, app, SosEntry));
  express.put(`/sos/entries/:id`, canView, express.crud.editRoute.bind(null, app, SosEntry));
  express.delete(`/sos/entries/:id`, canManage, express.crud.deleteRoute.bind(null, app, SosEntry));
};
