'use strict';

exports.DEFAULT_CONFIG = {
  mongooseId: 'mongoose',
  expressId: 'express',
  userId: 'user'
};

exports.recordTopics = {
  blacklist: ['subscriptions.added', 'subscriptions.edited', 'subscriptions.deleted']
};

exports.models = [
  require('./models/subscription'),
  require('user/models/user')
];

exports.optionalModules = {
  'mongoose user express': require('./routes')
};

exports.start = (app, module) =>
{
  app.broker.subscribe('users.deleted', message =>
  {
    module.Subscription.deleteMany({user: message.model._id.toString()}, err =>
    {
      if (err)
      {
        module.error(err, `Failed to delete subscriptions of deleted user.`, {
          deletedUser: message.model._id
        });
      }
    });
  });
};
