'use strict';

module.exports = async (app, module, req, res) =>
{
  const {
    Suggestion
  } = module;

  const rid = parseInt(req.query.rid, 10);

  if (isNaN(rid) || rid <= 0)
  {
    return res.sendStatus(400);
  }

  const suggestion = await Suggestion
    .findOne({rid: rid})
    .select({_id: 1})
    .lean()
    .exec();

  if (!suggestion)
  {
    return res.sendStatus(404);
  }

  res.json(suggestion._id);
};
