'use strict';

module.exports = (app, module, req, res, next) =>
{
  const loginAs = module.loginAs.get(req.query.secretKey);

  if (!loginAs)
  {
    return res.redirect('/');
  }

  clearTimeout(loginAs.timer);
  module.loginAs.delete(req.query.secretKey);

  if (req.session && req.session.user && req.session.user._id === loginAs.user._id.toString())
  {
    return res.redirect('/');
  }

  module.User.findById(loginAs.user._id).exec((err, user) =>
  {
    if (err)
    {
      module.error(err, `Failed to find a user to login as.`);
    }

    if (err || !user)
    {
      return res.redirect('/');
    }

    req.user = user.toObject();

    module.login(req, res, next);
  });
};
