'use strict';

const locks = new Map();

exports.create = (key, callback) =>
{
  if (!locks.has(key))
  {
    locks.set(key, []);

    setImmediate(callback);
  }
  else
  {
    locks.get(key).push(callback);
  }

  let released = false;

  return () =>
  {
    if (released)
    {
      return;
    }

    released = true;

    const callbacks = locks.get(key);

    if (!callbacks)
    {
      return;
    }

    const callback = callbacks.shift();

    if (!callbacks.length)
    {
      locks.delete(key);
    }

    if (callback)
    {
      setImmediate(callback);
    }
  };
};
