'use strict';

const moment = require('moment');
const {formatDate} = require('./dateFormatter');

exports.SHIFT_LENGTH = 8;

exports.SHIFT_COUNT = 24 / exports.SHIFT_LENGTH;

exports.SHIFT_START_HOUR = [null, 6, 14, 22];

exports.getShiftInfo = (dateOrTime, utc) =>
{
  const shiftMoment = utc ? moment.utc(dateOrTime) : moment(dateOrTime);
  const h = shiftMoment.hours();
  const shiftInfo = {
    moment: shiftMoment,
    date: null,
    ymd: '',
    startTime: null,
    endTime: null,
    startHour: 0,
    no: 0
  };

  if (h >= exports.SHIFT_START_HOUR[1] && h < exports.SHIFT_START_HOUR[2])
  {
    shiftInfo.no = 1;
  }
  else if (h >= exports.SHIFT_START_HOUR[2] && h < exports.SHIFT_START_HOUR[3])
  {
    shiftInfo.no = 2;
  }
  else
  {
    shiftInfo.no = 3;

    if (h < 6)
    {
      shiftMoment.subtract(1, 'days');
    }
  }

  shiftInfo.startHour = exports.SHIFT_START_HOUR[shiftInfo.no];

  shiftInfo.moment.hours(shiftInfo.startHour).startOf('hour');

  shiftInfo.date = shiftInfo.moment.toDate();
  shiftInfo.ymd = formatDate(shiftInfo.date);
  shiftInfo.startTime = shiftInfo.date.getTime();
  shiftInfo.endTime = shiftInfo.startTime + exports.SHIFT_LENGTH * 3600 * 1000;

  return shiftInfo;
};

exports.getCurrentShiftInfo = (utc) =>
{
  return exports.getShiftInfo(new Date(), !!utc);
};

exports.getShiftNo = (dateOrTime, utc) =>
{
  const date = typeof dateOrTime === 'number' ? new Date(dateOrTime) : dateOrTime;
  const h = date[utc ? 'getUTCHours' : 'getHours']();

  if (h >= 6 && h < 14)
  {
    return 1;
  }

  if (h >= 14 && h < 22)
  {
    return 2;
  }

  return 3;
};

exports.getCurrentDate = (utc) =>
{
  const currentDate = moment();

  if (currentDate.hours() < exports.SHIFT_START_HOUR[1])
  {
    currentDate.subtract(1, 'days');
  }

  currentDate.startOf('day');

  return utc ? currentDate.utc(true) : currentDate;
};

exports.getPlanDate = (input, utc) =>
{
  const m = utc ? moment.utc(input) : moment(input);

  if (m.hours() < exports.SHIFT_START_HOUR[1])
  {
    m.subtract(1, 'days');
  }

  m.startOf('day');

  return m;
};

exports.getFirstShiftStartTime = (time, utc) =>
{
  const date = new Date(time);
  let setHours = 'setUTCHours';
  let getHours = 'getUTCHours';

  if (!utc)
  {
    setHours = 'setHours';
    getHours = 'getHours';
  }

  const h = date[getHours]();

  if (h < 6)
  {
    date[setHours](0, 0, 0, 0);
    date[setHours](-(2 + 8 + 8));
  }
  else
  {
    date[setHours](6, 0, 0, 0);
  }

  return date.getTime();
};
