'use strict';

exports.name = 'VendorNc12';

exports.setUp = (app, mongoose) =>
{
  const vendorNc12Schema = new mongoose.Schema({
    vendor: {
      type: String,
      ref: 'Vendor',
      required: true
    },
    nc12: {
      type: String,
      required: true
    },
    value: String,
    unit: String
  }, {
    id: false
  });

  vendorNc12Schema.statics.TOPIC_PREFIX = 'vendorNc12s';

  vendorNc12Schema.index({vendor: 1, nc12: 1}, {unique: true});

  mongoose.model(exports.name, vendorNc12Schema);
};
