'use strict';

exports.name = 'VisNodePosition';

exports.setUp = (app, mongoose) =>
{
  const visNodePositionSchema = new mongoose.Schema({
    _id: String,
    x: Number,
    y: Number
  }, {
    id: false
  });

  visNodePositionSchema.statics.TOPIC_PREFIX = 'vis.nodePositions';
  visNodePositionSchema.statics.BROWSE_LIMIT = Number.MAX_SAFE_INTEGER;

  mongoose.model(exports.name, visNodePositionSchema);
};
