'use strict';

exports.name = 'OldWhCancelQueue';

exports.setUp = (app, mongoose) =>
{
  const cancelQueueSchema = new mongoose.Schema({
    _id: String
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  cancelQueueSchema.statics.TOPIC_PREFIX = 'old.wh.cancelQueue';
  cancelQueueSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, cancelQueueSchema);
};
