'use strict';

exports.name = 'OldWhDeliveredOrder';

exports.setUp = (app, mongoose) =>
{
  const deliveredOrderSchema = new mongoose.Schema({
    status: {
      type: String,
      enum: ['todo', 'done', 'blocked']
    },
    line: String,
    qtyTodo: Number,
    qtyDone: Number,
    qtyRemaining: Number,
    pceTime: Number,
    sapOrder: String,
    whOrder: String,
    date: Date,
    set: Number,
    startTime: Date
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  deliveredOrderSchema.statics.TOPIC_PREFIX = 'old.wh.deliveredOrders';
  deliveredOrderSchema.statics.BROWSE_LIMIT = 100;

  deliveredOrderSchema.index({sapOrder: 1, line: 1, status: 1});
  deliveredOrderSchema.index({line: 1, status: 1});
  deliveredOrderSchema.index({status: 1});

  mongoose.model(exports.name, deliveredOrderSchema);
};
