'use strict';

exports.name = 'OldWhEvent';

exports.setUp = (app, mongoose) =>
{
  const whEventSchema = new mongoose.Schema({
    type: {
      type: String,
      required: true
    },
    time: {
      type: Date,
      required: true
    },
    user: {
      type: {},
      required: true
    },
    order: {
      type: String,
      default: null,
      ref: 'OldWhOrder'
    },
    data: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  whEventSchema.statics.TOPIC_PREFIX = 'old.wh.events';
  whEventSchema.statics.BROWSE_LIMIT = 100;

  whEventSchema.index({time: -1});
  whEventSchema.index({type: 1, time: -1});
  whEventSchema.index({order: 1, time: -1});
  whEventSchema.index({'user.id': 1, time: -1});

  whEventSchema.post('save', function(doc)
  {
    app.broker.publish(`${whEventSchema.statics.TOPIC_PREFIX}.saved`, {
      events: [doc.toJSON()]
    });
  });

  whEventSchema.statics.record = function(events, session)
  {
    const WhEvent = this;

    return WhEvent.insertMany(events, {session: session || null, lean: true}).then(() => ({
      added: events,
      messages: [{
        topic: `${WhEvent.TOPIC_PREFIX}.saved`,
        message: {events}
      }]
    }));
  };

  mongoose.model(exports.name, whEventSchema);
};
