'use strict';

exports.name = 'OldWhLine';

exports.setUp = (app, mongoose) =>
{
  const lineSchema = new mongoose.Schema({
    _id: String,
    pickup: {
      pending: {
        orders: Number,
        sets: Number,
        pce: Number,
        time: Number
      },
      orders: Number,
      sets: Number,
      pce: Number,
      time: Number
    },
    components: {
      pce: Number,
      time: Number
    },
    packaging: {
      pce: Number,
      time: Number
    }
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  lineSchema.statics.TOPIC_PREFIX = 'old.wh.lines';
  lineSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, lineSchema);
};
