'use strict';

exports.name = 'OldWhLine';

exports.setUp = (app, mongoose) =>
{
  const number = {type: Number, default: 0};
  const lineSchema = new mongoose.Schema({
    _id: String,
    pickup: {
      sets: number,
      qty: number,
      time: number
    },
    components: {
      qty: number,
      time: number
    },
    packaging: {
      qty: number,
      time: number
    }
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  lineSchema.statics.TOPIC_PREFIX = 'old.wh.lines';
  lineSchema.statics.BROWSE_LIMIT = 1000;

  lineSchema.statics.lockLines = async function(lines)
  {
    const WhLine = this;

    return Promise.all(lines.map(({_id}) => app.wh.state.lock(`${WhLine.TOPIC_PREFIX}.${_id}`)));
  };

  lineSchema.statics.startNewSet = async function(setOrders, session)
  {
    const WhLine = this;

    const result = {
      updated: [],
      messages: []
    };

    const lineList = await WhLine
      .find({_id: {$in: setOrders[0].lines.map(({_id}) => _id)}})
      .lean()
      .session(session)
      .exec();
    const lineMap = new Map();
    const operations = [];

    lineList.forEach(line =>
    {
      result.updated.push(line);

      lineMap.set(line._id, line);

      operations.push({
        updateOne: {
          filter: {_id: line._id},
          update: {$set: {
            pickup: line.pickup
          }}
        }
      });

      line.pickup.sets += 1;
    });

    setOrders.forEach(whOrder =>
    {
      whOrder.lines.forEach(whOrderLine =>
      {
        const line = lineMap.get(whOrderLine._id);

        line.pickup.qty += whOrderLine.qty;
        line.pickup.time += whOrderLine.qty * whOrderLine.pceTime;
      });
    });

    await WhLine.collection.bulkWrite(operations, {session});

    result.messages.push({
      topic: `${WhLine.TOPIC_PREFIX}.updated`,
      message: {
        updated: result.updated
      }
    });

    return result;
  };

  lineSchema.statics.recountPickupByWhOrder = async function(whOrder)
  {
    const WhLine = this;

    await WhLine.lockLines(whOrder.lines);

    return Promise.all(
      whOrder.lines.map(
        whOrderLine => WhLine.recountPickup(whOrderLine._id, whOrder.$session())
      )
    );
  };

  lineSchema.statics.recountPickup = async function(lineId, session)
  {
    const WhLine = this;
    const WhOrder = mongoose.model('OldWhOrder');

    const result = {
      updated: [],
      messages: []
    };

    const [whLine, whOrders] = await Promise.all([
      WhLine
        .findById(lineId)
        .lean()
        .session(session)
        .exec(),
      WhOrder
        .find({
          distStatus: 'pending',
          'lines._id': lineId,
          status: {$in: ['started', 'finished']}
        })
        .select({status: 1, date: 1, lines: 1, set: 1})
        .lean()
        .session(session)
        .exec()
    ]);

    if (!whLine)
    {
      return result;
    }

    const {pickup} = whLine;

    pickup.sets = new Set();
    pickup.qty = 0;
    pickup.time = 0;

    whOrders.forEach(whOrder =>
    {
      const whOrderLine = whOrder.lines.find(({_id}) => _id === whLine._id);

      if (!whOrderLine)
      {
        return;
      }

      pickup.sets.add(`${whOrder.date.getTime()}:${whOrder.set}`);
      pickup.qty += whOrderLine.qty;
      pickup.time += whOrderLine.qty * whOrderLine.pceTime;
    });

    pickup.sets = pickup.sets.size;

    await WhLine.collection.updateOne({_id: whLine._id}, {$set: {pickup}});

    result.updated.push(whLine);

    result.messages.push({
      topic: `${WhLine.TOPIC_PREFIX}.updated`,
      message: {
        updated: result.updated
      }
    });

    return result;
  };

  mongoose.model(exports.name, lineSchema);
};
