'use strict';

exports.name = 'OldWhLineSnapshot';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    line: String,
    time: Date,
    data: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  schema.statics.TOPIC_PREFIX = 'old.wh.lineSnapshots';
  schema.statics.BROWSE_LIMIT = 100;

  schema.index({line: 1, time: -1});
  schema.index({time: -1});

  return schema;
};
