'use strict';

exports.name = 'OldWhLineSnapshot';

exports.setUp = (app, mongoose) =>
{
  const snapshotSchema = new mongoose.Schema({
    line: String,
    time: Date,
    data: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  snapshotSchema.statics.TOPIC_PREFIX = 'old.wh.lineSnapshots';
  snapshotSchema.statics.BROWSE_LIMIT = 100;

  snapshotSchema.index({line: 1, time: -1});
  snapshotSchema.index({time: -1});

  mongoose.model(exports.name, snapshotSchema);
};
