'use strict';

exports.name = 'OldWhPendingPackaging';

exports.setUp = (app, mongoose) =>
{
  const pendingPackagingSchema = new mongoose.Schema({
    setCarts: [mongoose.Schema.Types.ObjectId],
    whOrders: [String]
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  pendingPackagingSchema.statics.TOPIC_PREFIX = 'old.wh.pending.packaging';
  pendingPackagingSchema.statics.BROWSE_LIMIT = 0;

  mongoose.model(exports.name, pendingPackagingSchema);
};
