'use strict';

exports.name = 'OldWhPendingSetAssign';

exports.setUp = (app, mongoose) =>
{
  const pendingSetAssignSchema = new mongoose.Schema({
    date: Date,
    set: Number,
    fmx: Boolean,
    kitter: Boolean,
    packer: Boolean,
    platformer: Boolean,
    painter: Boolean
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  pendingSetAssignSchema.statics.TOPIC_PREFIX = 'old.wh.pendingSetAssigns';
  pendingSetAssignSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, pendingSetAssignSchema);
};
