'use strict';

exports.name = 'OldWhPlanChange';

exports.setUp = (app, mongoose) =>
{
  const changeSchema = new mongoose.Schema({
    time: Date,
    plan: Date,
    added: [{}],
    changed: [{}],
    removed: [{}]
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  changeSchema.statics.TOPIC_PREFIX = 'old.wh.planChanges';
  changeSchema.statics.BROWSE_LIMIT = 100;

  changeSchema.index({time: -1});
  changeSchema.index({plan: -1});

  mongoose.model(exports.name, changeSchema);
};
