'use strict';

exports.name = 'OldWhRedirReason';

exports.setUp = (app, mongoose) =>
{
  const redirReasonSchema = new mongoose.Schema({
    active: Boolean,
    label: String
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  redirReasonSchema.statics.TOPIC_PREFIX = 'old.wh.redirReasons';
  redirReasonSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, redirReasonSchema);
};
