'use strict';

exports.name = 'OldWhUser';

exports.setUp = (app, mongoose) =>
{
  const userSchema = new mongoose.Schema({
    _id: String,
    label: String,
    func: String,
    lastPickupAt: Date,
    lastDeliveryAt: Date
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  userSchema.statics.TOPIC_PREFIX = 'old.wh.users';
  userSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, userSchema);
};
