'use strict';

exports.name = 'OldWhUser';

exports.setUp = (app, mongoose) =>
{
  const whUserSchema = new mongoose.Schema({
    _id: String,
    label: String,
    func: String,
    lastPickupAt: Date
  }, {
    id: false,
    minimize: false
  });

  whUserSchema.statics.TOPIC_PREFIX = 'old.wh.users';
  whUserSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, whUserSchema);
};
