'use strict';

const moment = require('moment');

module.exports = async (app, module, req, res, next) =>
{
  const {
    OldWhOrder: WhOrder
  } = module;

  try
  {
    const whOrders = await WhOrder
      .find({
        date: moment.utc(req.body.date, 'YYYY-MM-DD').toDate(),
        mrp: req.body.mrp,
        status: {$in: ['started', 'finished']}
      })
      .select({_id: 1})
      .limit(1)
      .lean()
      .exec();

    res.json({
      locked: whOrders.length === 1
    });
  }
  catch (err)
  {
    return next(err);
  }
};
