'use strict';

const moment = require('moment');

module.exports = async (app, module, req, res, next) =>
{
  const {
    OldWhOrder: WhOrder
  } = module;

  try
  {
    const whOrder = await WhOrder
      .find({
        date: moment.utc(req.body.date, 'YYYY-MM-DD').toDate(),
        mrp: req.body.mrp
      })
      .select({_id: 1})
      .limit(1)
      .lean()
      .exec();

    res.json({
      locked: !!whOrder
    });
  }
  catch (err)
  {
    return next(err);
  }
};
