'use strict';

exports.detached = true;

exports.session = false;

exports.execute = async (app, module, {data}) =>
{
  const {
    OldWhOrder: WhOrder
  } = module;

  const whOrders = await WhOrder
    .find({
      order: data.orderNo,
      psStatus: {$ne: data.psStatus}
    })
    .select({_id: 1, psStatus: 1})
    .hint({order: 1})
    .lean()
    .exec();

  if (!whOrders.length)
  {
    return;
  }

  const ids = [];

  whOrders.forEach(whOrder =>
  {
    ids.push(whOrder._id);

    whOrder.psStatus = data.psStatus;
  });

  await WhOrder.collection.updateMany({_id: {$in: ids}}, {$set: {psStatus: data.psStatus}});

  return {
    messages: [{
      topic: `${WhOrder.TOPIC_PREFIX}.updated`,
      message: {updated: whOrders}
    }, {
      topic: `${WhOrder.TOPIC_PREFIX}.updated.psStatus`,
      message: {updated: whOrders}
    }]
  };
};
