'use strict';

exports.name = 'WmesClient';

exports.setUp = (app, mongoose) =>
{
  const clientSchema = new mongoose.Schema({
    _id: String,
    lastSeenAt: Date,
    ipAddress: String,
    config: {},
    versions: {},
    hardware: {},
    networkInterfaces: {}
  }, {
    id: false,
    minimize: false,
    versionKey: false
  });

  clientSchema.statics.TOPIC_PREFIX = 'wmes.clients';
  clientSchema.statics.BROWSE_LIMIT = 100;

  clientSchema.index({lastSeenAt: -1});

  mongoose.model(exports.name, clientSchema);
};
