'use strict';

const editClientRoute = require('./editClient');
const listUpdatesRoute = require('./listUpdates');
const serveUpdateRoute = require('./serveUpdate');

module.exports = (app, module) =>
{
  const {
    express,
    user,
    WmesClient
  } = module;

  const canView = user.auth('WMES:CLIENTS:VIEW');
  const canManage = user.auth('WMES:CLIENTS:MANAGE');

  express.options('/wmes/clients;time', cors, (req, res) => res.end());
  express.get('/wmes/clients;time', cors, (req, res) => res.json({time: Date.now()}));

  express.get('/wmes/clients/updates', listUpdatesRoute.bind(null, app, module));
  express.get('/wmes/clients/updates/:version', serveUpdateRoute.bind(null, app, module));

  express.get('/wmes/clients', canView, express.crud.browseRoute.bind(null, app, WmesClient));
  express.options('/wmes/clients/:id', cors, (req, res) => res.end());
  express.get('/wmes/clients/:id', cors, canView, express.crud.readRoute.bind(null, app, WmesClient));
  express.put('/wmes/clients/:id', cors, canManage, editClientRoute.bind(null, app, module));

  function cors(req, res, next)
  {
    const origin = String(req.headers.origin || '');

    if (/^https?:\/\/([a-z0-9.-]+\.)?wmes\.pl$/.test(origin))
    {
      res.set('Access-Control-Allow-Origin', origin);
    }

    res.set('Access-Control-Allow-Methods', 'GET, PUT');
    res.set('Access-Control-Allow-Credentials', 'true');
    res.set('Access-Control-Allow-Headers', req.headers['access-control-request-headers'] || '');

    next();
  }
};
