'use strict';

const path = require('path');

module.exports = (app, module, req, res, next) =>
{
  if (!/^[0-9]{1,10}$/.test(req.params.version))
  {
    return next(app.createError('Invalid version.', 'INPUT', 400));
  }

  res.sendFile(path.join(module.config.updatesPath, `${req.params.version}.7z`));
};
