'use strict';

exports.DEFAULT_CONFIG = {
  mongooseId: 'mongoose',
  expressId: 'express',
  userId: 'user',
  settingsId: 'settings',
  morId: 'mor',
  mailSenderId: 'mail/sender',
  uploadsDest: './data/uploads/compRel'
};

exports.models = [
  require('./models/compRelEntry'),
  require('./models/compRelFunc'),
  require('./models/compRelReason'),
  require('orders/models/order'),
  require('orders/models/component'),
  require('mrpControllers/models/mrpController'),
  require('user/models/user')
];

exports.userPrivileges = [
  'COMP_REL:VIEW',
  'COMP_REL:ADD',
  'COMP_REL:MANAGE'
];

exports.republishTopics = [
  'compRel.entries.added', 'compRel.entries.updated.*', 'compRel.entries.deleted',
  'compRel.funcs.added', 'compRel.funcs.edited', 'compRel.funcs.deleted',
  'compRel.reasons.added', 'compRel.reasons.edited', 'compRel.reasons.deleted',
  'compRel.orders.updated.*'
];

exports.recordTopics = {
  debug: [
    'compRel.funcs.added', 'compRel.funcs.edited',
    'compRel.reasons.added', 'compRel.reasons.edited'
  ],
  warning: [
    'compRel.entries.deleted',
    'compRel.funcs.deleted',
    'compRel.reasons.deleted'
  ]
};

exports.requiredModules = 'mongoose';

exports.optionalModules = {
  'user express settings': require('./routes'),
  'mailSender': require('./notifier')
};

exports.start = (app, module) =>
{
  module.DICTIONARIES = {
    funcs: {model: module.CompRelFunc},
    reasons: {model: module.CompRelReason}
  };

  module.tmpAttachments = {};

  app.broker.subscribe(`${module.Order.TOPIC_PREFIX}.updated.*`, onOrderUpdated);

  function onOrderUpdated({_id, change})
  {
    if (!change.newValues.compRels)
    {
      return;
    }

    app.broker.publish(`compRel.orders.updated.${_id}`, {
      orderNo: _id,
      compRels: change.newValues.compRels
    });
  }
};
