'use strict';

const userInfoSchema = require('user/models/userInfoSchema');

exports.name = 'CompRelFunc';

exports.setUp = (app, mongoose) =>
{
  const funcSchema = new mongoose.Schema({
    _id: String,
    mor: {
      type: String,
      enum: ['none', 'mrp', 'all'],
      default: 'none'
    },
    users: [userInfoSchema]
  }, {
    id: false,
    minimize: false
  });

  funcSchema.statics.TOPIC_PREFIX = 'compRel.funcs';
  funcSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, funcSchema);
};
