'use strict';

exports.name = 'CompRelReason';

exports.setUp = (app, mongoose) =>
{
  const reasonSchema = new mongoose.Schema({
    name: {
      type: String,
      required: true,
      trim: true
    },
    active: {
      type: Boolean,
      default: true
    }
  }, {
    id: false,
    minimize: false
  });

  reasonSchema.statics.TOPIC_PREFIX = 'compRel.reasons';
  reasonSchema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, reasonSchema);
};
