'use strict';

module.exports = (app, {CompRelEntry}, req, res, next) =>
{
  if (/^[0-9]+$/.test(req.params.filter))
  {
    redirectToDetails(req, res, next);
  }
  else
  {
    redirectToList(req, res, next);
  }

  function redirectToDetails(req, res, next)
  {
    CompRelEntry.findOne({rid: parseInt(req.params.filter, 10)}, {_id: 1}).lean().exec((err, entry) =>
    {
      if (err)
      {
        return next(err);
      }

      if (entry)
      {
        return res.redirect(`/#compRel/entries/${entry._id}`);
      }

      res.sendStatus(404);
    });
  }

  function redirectToList(req, res)
  {
    let url = '/#compRel/entries';

    if (req.params.filter === 'mine')
    {
      url += '?exclude(changes)&limit(-1337)&sort(-createdAt)&users=mine';
    }

    res.redirect(url);
  }
};
