'use strict';

const step = require('h5.step');

module.exports = (app, {Component}, req, res, next) =>
{
  step(
    function()
    {
      Component
        .findById(req.query.nc12)
        .lean()
        .exec(this.next());
    },
    function(err, component)
    {
      if (err)
      {
        return next(err);
      }

      if (!component)
      {
        return res.sendStatus(404);
      }

      res.json(component);
    }
  );
};
