'use strict';

exports.name = 'CtCart';

exports.setUp = (app, mongoose) =>
{
  const cartSchema = new mongoose.Schema({
    type: {
      type: String,
      enum: ['other', 'balluff'],
      default: 'other'
    },
    description: String,
    cards: [String]
  }, {
    id: false,
    minimize: false
  });

  cartSchema.statics.TOPIC_PREFIX = 'ct.carts';
  cartSchema.statics.BROWSE_LIMIT = 100;

  cartSchema.index({cards: 1});
  cartSchema.index({type: 1});

  mongoose.model(exports.name, cartSchema);
};
