'use strict';

exports.name = 'CtLine';

exports.setUp = (app, mongoose) =>
{
  const lineSchema = new mongoose.Schema({
    _id: String,
    active: {
      type: Boolean,
      required: true
    },
    type: {
      type: String,
      enum: ['inveo', 'luma2'],
      required: true
    },
    stations: [{}],
    state: {
      updatedAt: Number,
      stations: {}
    }
  }, {
    id: false,
    minimize: false
  });

  lineSchema.statics.TOPIC_PREFIX = 'ct.lines';
  lineSchema.statics.BROWSE_LIMIT = 100;

  lineSchema.index({active: true});

  lineSchema.statics.customizeLeanObject = function(leanModel)
  {
    leanModel.state = undefined;

    return leanModel;
  };

  mongoose.model(exports.name, lineSchema);
};
